  email: 'user@sukusuku.ai',
  firstName: 'User',
  lastName: '',
  imagegeneCredits: 50,
  profileImageUrl: null
});

// PASTE THIS useEffect HOOK (replace any existing auth logic)
useEffect(() => {
  console.log('ImageGene: Checking for authentication token...');

  // Get token from URL
  const urlParams = new URLSearchParams(window.location.search);
  const token = urlParams.get('token');

  if (token) {
    console.log('ImageGene: Token found in URL:', token.substring(0, 50) + '...');

    // Decode the JWT token
    const decoded = decodeJWT(token);
    console.log('ImageGene: Decoded token data:', decoded);

    if (decoded && decoded.email) {
      const newUserData = {
        email: decoded.email,
        firstName: decoded.firstName || 'User',
