      sessionStorage.setItem('imagegene_auth_token', token);
    }
  } else {
    // Check sessionStorage
    const storedUserData = sessionStorage.getItem('imagegene_user_data');
    if (storedUserData) {
      try {
        const userData = JSON.parse(storedUserData);
        console.log('ImageGene: Loaded from storage:', userData);
        setUser(userData);
      } catch (error) {
        console.error('ImageGene: Storage parse error:', error);
      }
    }
  }
}, []);
