      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));
      return JSON.parse(jsonPayload);
    } catch (error) {
      console.error('Error decoding JWT:', error);
      return null;
    }
  }

  useEffect(() => {
    // Check URL parameters first
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get('token');

    if (token) {
      console.log('Token received:', token);
      // Decode JWT token from sukusuku.ai
      const decoded = decodeJWT(token);
      console.log('Decoded user data:', decoded);

      if (decoded) {
        setUserInfo({
          email: decoded.email || 'user@sukusuku.ai',
          firstName: decoded.firstName || 'User',
          lastName: decoded.lastName || '',
          penoraCredits: decoded.penoraCredits || 100,
          imagegeneCredits: decoded.imagegeneCredits || 50,
          profileImageUrl: decoded.profileImageUrl || null
        });

        // Store for future use
        sessionStorage.setItem('sukusuku_auth_token', token);
        sessionStorage.setItem('sukusuku_user_data', JSON.stringify(decoded));
      }
    } else {
      // Check sessionStorage for existing data
      const storedUserData = sessionStorage.getItem('sukusuku_user_data');
      if (storedUserData) {
        try {
          const userData = JSON.parse(storedUserData);
          setUserInfo({
            email: userData.email || 'user@sukusuku.ai',
            firstName: userData.firstName || 'User',
            lastName: userData.lastName || '',
            penoraCredits: userData.penoraCredits || 100,
            imagegeneCredits: userData.imagegeneCredits || 50,
            profileImageUrl: userData.profileImageUrl || null
          });
        } catch (error) {
          console.error('Error parsing stored user data:', error);
        }
      }
    }
  }, []);

  return (
    <div className="user-profile">
      {/* User display */}
      <div className="flex items-center space-x-2">
        {userInfo.profileImageUrl ? (
          <img src={userInfo.profileImageUrl} alt="Profile" className="w-8 h-8 rounded-full" />
        ) : (
          <div className="w-8 h-8 rounded-full bg-red-500 flex items-center justify-center text-white text-sm">
            {userInfo.firstName?.[0]}{userInfo.lastName?.[0]}
          </div>
        )}
        <span className="text-white font-medium">{userInfo.firstName}</span>
        <span className="text-purple-400">{userInfo.imagegeneCredits} credits</span>
      </div>
    </div>
  );
}

export default UserProfile;
```

## 5. Debug logs (add these temporarily to verify data is being received):

```javascript
