import {
  users,
  authTokens,
  type User,
  type UpsertUser,
  type AuthToken,
  type InsertAuthToken,
} from "@shared/schema";
import { db } from "./db";
import { eq, and } from "drizzle-orm";

// Interface for storage operations
export interface IStorage {
  // User operations
  // (IMPORTANT) these user operations are mandatory for Replit Auth.
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<UpsertUser>): Promise<User | undefined>;
  updateUserCredits(id: string, penoraCredits?: number, imagegeneCredits?: number): Promise<User | undefined>;

  // Token operations
  createAuthToken(token: InsertAuthToken): Promise<AuthToken>;
  getAuthToken(token: string): Promise<AuthToken | undefined>;
  deleteAuthToken(token: string): Promise<void>;
  deleteUserTokens(userId: string, type?: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  // (IMPORTANT) these user operations are mandatory for Replit Auth.

  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    try {
      // First try to find existing user by email or id
      let existingUser = null;
      if (userData.email) {
        [existingUser] = await db.select().from(users).where(eq(users.email, userData.email));
      }
      if (!existingUser && userData.id) {
        [existingUser] = await db.select().from(users).where(eq(users.id, userData.id));
      }

      if (existingUser) {
        // Update existing user
        const [updatedUser] = await db
          .update(users)
          .set({
            ...userData,
            updatedAt: new Date(),
            lastLoginAt: new Date(),
          })
          .where(eq(users.id, existingUser.id))
          .returning();
        return updatedUser;
      } else {
        // Create new user with default credits
        const [newUser] = await db
          .insert(users)
          .values({
            ...userData,
            penoraCredits: 100,
            imagegeneCredits: 50,
            totalCreditsUsed: 0,
            lastLoginAt: new Date(),
          })
          .returning();

        // Send welcome email to new users
        if (newUser.email) {
          try {
            const { sendWelcomeEmail } = await import('./emailService');
            await sendWelcomeEmail({
              to: newUser.email,
              firstName: newUser.firstName || 'Friend'
            });
            console.log('Welcome email sent to:', newUser.email);
          } catch (error) {
            console.error('Failed to send welcome email:', error);
          }
        }

        return newUser;
      }
    } catch (error) {
      console.error('Error in upsertUser:', error);
      throw error;
    }
  }

  async updateUserCredits(id: string, penoraCredits?: number, imagegeneCredits?: number): Promise<User | undefined> {
    const updateData: any = { updatedAt: new Date() };
    if (penoraCredits !== undefined) updateData.penoraCredits = penoraCredits;
    if (imagegeneCredits !== undefined) updateData.imagegeneCredits = imagegeneCredits;

    const [user] = await db
      .update(users)
      .set(updateData)
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async updateUser(id: string, updates: Partial<UpsertUser>): Promise<User | undefined> {
    const [updatedUser] = await db
      .update(users)
      .set({
        ...updates,
        updatedAt: new Date(),
      })
      .where(eq(users.id, id))
      .returning();
    return updatedUser;
  }

  async sendWelcomeEmail(user: User): Promise<void> {
    try {
      const nodemailer = (await import('nodemailer')).default;

      if (!process.env.GMAIL_USERNAME || !process.env.GMAIL_APP_PASSWORD) {
        console.log('Gmail credentials not configured for welcome email');
        return;
      }

      const transporter = nodemailer.createTransport({
        service: 'gmail',
        auth: {
          user: process.env.GMAIL_USERNAME,
          pass: process.env.GMAIL_APP_PASSWORD
        }
      });

      const firstName = user.firstName || 'Creator';

      const mailOptions = {
        from: 'hello@sukusuku.ai',
        to: user.email,
        replyTo: 'hello@sukusuku.ai',
        subject: 'üöÄ Welcome to SukuSuku.ai ‚Äî Let\'s Create Stories That Matter!',
        html: `
