                    onClick={copyToClipboard}
                    variant="outline"
                    size="sm"
                    className="border-suku-border text-suku-text-secondary hover:bg-suku-red hover:text-white"
                  >
                    <Copy className="w-4 h-4 mr-1" />
                    Copy
                  </Button>
                  <Button
                    onClick={downloadText}
                    variant="outline"
                    size="sm"
                    className="border-suku-border text-suku-text-secondary hover:bg-suku-red hover:text-white"
